private void setDescription(org.apache.commons.configuration.Configuration configuration, org.apache.servicecomb.serviceregistry.api.registry.Microservice microservice) {
    java.lang.String[] descriptionArray = configuration.getStringArray(org.apache.servicecomb.serviceregistry.api.registry.CONFIG_QUALIFIED_MICROSERVICE_DESCRIPTION_KEY);
    if ((null == descriptionArray) || (descriptionArray.length < 1)) {
        return;
    }
    java.lang.StringBuilder rawDescriptionBuilder = new java.lang.StringBuilder();
    for (java.lang.String desc : descriptionArray) {
        rawDescriptionBuilder.append(desc).append(",");
    }
    microservice.setDescription(rawDescriptionBuilder.substring(0, rawDescriptionBuilder.length() - 1));
}

