public class MicroserviceFactory {
    public org.apache.servicecomb.serviceregistry.api.registry.Microservice create(java.lang.String appId, java.lang.String microserviceName) {
        org.apache.servicecomb.serviceregistry.definition.MicroserviceDefinition microserviceDefinition = org.apache.servicecomb.serviceregistry.definition.MicroserviceDefinition.create(appId, microserviceName);
        return create(microserviceDefinition);
    }

    public org.apache.servicecomb.serviceregistry.api.registry.Microservice create(org.apache.servicecomb.serviceregistry.definition.MicroserviceDefinition microserviceDefinition) {
        org.apache.commons.configuration.Configuration configuration = microserviceDefinition.getConfiguration();
        org.apache.servicecomb.serviceregistry.api.registry.Microservice microservice = createMicroserviceFromDefinition(configuration);
        microservice.setInstance(org.apache.servicecomb.serviceregistry.api.registry.MicroserviceInstance.createFromDefinition(configuration));
        return microservice;
    }

    private org.apache.servicecomb.serviceregistry.api.registry.Microservice createMicroserviceFromDefinition(org.apache.commons.configuration.Configuration configuration) {
        org.apache.servicecomb.serviceregistry.api.registry.Microservice microservice = new org.apache.servicecomb.serviceregistry.api.registry.Microservice();
        microservice.setServiceName(configuration.getString(org.apache.servicecomb.serviceregistry.api.registry.CONFIG_QUALIFIED_MICROSERVICE_NAME_KEY, org.apache.servicecomb.serviceregistry.api.registry.DEFAULT_MICROSERVICE_NAME));
        microservice.setAppId(configuration.getString(org.apache.servicecomb.serviceregistry.api.registry.CONFIG_APPLICATION_ID_KEY, org.apache.servicecomb.serviceregistry.api.registry.DEFAULT_APPLICATION_ID));
        microservice.setVersion(configuration.getString(org.apache.servicecomb.serviceregistry.api.registry.CONFIG_QUALIFIED_MICROSERVICE_VERSION_KEY, org.apache.servicecomb.serviceregistry.api.registry.DEFAULT_MICROSERVICE_VERSION));
        setDescription(configuration, microservice);
        microservice.setLevel(configuration.getString(org.apache.servicecomb.serviceregistry.api.registry.CONFIG_QUALIFIED_MICROSERVICE_ROLE_KEY, "FRONT"));
        microservice.setPaths(org.apache.servicecomb.serviceregistry.config.ConfigurePropertyUtils.getMicroservicePaths(configuration));
        java.util.Map<java.lang.String, java.lang.String> propertiesMap = MicroservicePropertiesLoader.INSTANCE.loadProperties(configuration);
        microservice.setProperties(propertiesMap);
        if (allowCrossApp(propertiesMap)) {
            microservice.setAlias(org.apache.servicecomb.serviceregistry.api.registry.Microservice.generateAbsoluteMicroserviceName(microservice.getAppId(), microservice.getServiceName()));
        }
        org.apache.servicecomb.serviceregistry.api.registry.Framework framework = new org.apache.servicecomb.serviceregistry.api.registry.Framework();
        framework.setName(org.apache.servicecomb.serviceregistry.api.registry.CONFIG_FRAMEWORK_DEFAULT_NAME);
        framework.setVersion(org.apache.servicecomb.serviceregistry.api.registry.CONFIG_FRAMEWORK_DEFAULT_VERSION);
        microservice.setFramework(framework);
        microservice.setRegisterBy(org.apache.servicecomb.serviceregistry.api.registry.CONFIG_DEFAULT_REGISTER_BY);
        return microservice;
    }

    private void setDescription(org.apache.commons.configuration.Configuration configuration, org.apache.servicecomb.serviceregistry.api.registry.Microservice microservice) {
        java.lang.String[] descriptionArray = configuration.getStringArray(org.apache.servicecomb.serviceregistry.api.registry.CONFIG_QUALIFIED_MICROSERVICE_DESCRIPTION_KEY);
        if ((null == descriptionArray) || (descriptionArray.length < 1)) {
            return;
        }
        java.lang.StringBuilder rawDescriptionBuilder = new java.lang.StringBuilder();
        for (java.lang.String desc : descriptionArray) {
            rawDescriptionBuilder.append(desc).append(",");
        }
        microservice.setDescription(rawDescriptionBuilder.substring(0, rawDescriptionBuilder.length() - 1));
    }

    private boolean allowCrossApp(java.util.Map<java.lang.String, java.lang.String> propertiesMap) {
        return java.lang.Boolean.valueOf(propertiesMap.get(org.apache.servicecomb.serviceregistry.api.registry.CONFIG_ALLOW_CROSS_APP_KEY));
    }
}
