public synchronized void start() throws java.io.IOException, java.lang.InterruptedException {
    if (zooKeeperProcess != null) {
        throw new java.lang.IllegalStateException("Already started");
    }
    java.lang.Runtime.getRuntime().addShutdownHook(new java.lang.Thread() {
        @java.lang.Override
        public void run() {
            try {
                MiniAccumuloCluster.this.stop();
            } catch (java.io.IOException e) {
                e.printStackTrace();
            } catch (java.lang.InterruptedException e) {
                e.printStackTrace();
            }
        }
    });
    zooKeeperProcess = exec(org.apache.accumulo.start.Main.class, org.apache.zookeeper.server.ZooKeeperServerMain.class.getName(), zooCfgFile.getAbsolutePath());
    org.apache.accumulo.core.util.UtilWaitThread.sleep(250);
    java.lang.Process initProcess = exec(org.apache.accumulo.server.util.Initialize.class, "--instance-name", org.apache.accumulo.minicluster.MiniAccumuloCluster.INSTANCE_NAME, "--password", config.getRootPassword());
    int ret = initProcess.waitFor();
    if (ret != 0) {
        throw new java.lang.RuntimeException(((("Initialize process returned " + ret) + ". Check the logs in ") + logDir) + " for errors.");
    }
    tabletServerProcesses = new java.lang.Process[config.getNumTservers()];
    for (int i = 0; i < config.getNumTservers(); i++) {
        tabletServerProcesses[i] = exec(org.apache.accumulo.server.tabletserver.TabletServer.class);
    }
    masterProcess = exec(org.apache.accumulo.server.master.Master.class);
    gcProcess = exec(org.apache.accumulo.server.gc.SimpleGarbageCollector.class);
    if (null == executor) {
        executor = java.util.concurrent.Executors.newSingleThreadExecutor();
    }
}

public synchronized void stop() throws java.io.IOException, java.lang.InterruptedException {
    if (null == executor) {
        return;
    }
    if (zooKeeperProcess != null) {
        try {
            stopProcessWithTimeout(zooKeeperProcess, 30, java.util.concurrent.TimeUnit.SECONDS);
        } catch (java.util.concurrent.ExecutionException e) {
