                org.apache.accumulo.minicluster.MiniAccumuloCluster.log.warn("TabletServer did not fully stop after 30 seconds", e);
            }
        }
    }
    for (org.apache.accumulo.minicluster.MiniAccumuloCluster.LogWriter lw : logWriters) {
        lw.flush();
    }
    if (gcProcess != null) {
        try {
            stopProcessWithTimeout(gcProcess, 30, java.util.concurrent.TimeUnit.SECONDS);
        } catch (java.util.concurrent.ExecutionException e) {
            org.apache.accumulo.minicluster.MiniAccumuloCluster.log.warn("GarbageCollector did not fully stop after 30 seconds", e);
        } catch (java.util.concurrent.TimeoutException e) {
            org.apache.accumulo.minicluster.MiniAccumuloCluster.log.warn("GarbageCollector did not fully stop after 30 seconds", e);
        }
    }
