public class MockAccumulo {
    final java.util.Map<java.lang.String, org.apache.accumulo.core.client.mock.MockTable> tables = new java.util.HashMap<java.lang.String, org.apache.accumulo.core.client.mock.MockTable>();

    final java.util.Map<java.lang.String, java.lang.String> systemProperties = new java.util.HashMap<java.lang.String, java.lang.String>();

    java.util.Map<java.lang.String, org.apache.accumulo.core.client.mock.MockUser> users = new java.util.HashMap<java.lang.String, org.apache.accumulo.core.client.mock.MockUser>();

    final org.apache.hadoop.fs.FileSystem fs;

    final java.util.concurrent.atomic.AtomicInteger tableIdCounter = new java.util.concurrent.atomic.AtomicInteger(0);

    MockAccumulo(org.apache.hadoop.fs.FileSystem fs) {
        this.fs = fs;
    }

    {
        org.apache.accumulo.core.client.mock.MockUser root = new org.apache.accumulo.core.client.mock.MockUser("root", new org.apache.accumulo.core.client.security.tokens.PasswordToken(new byte[0]), org.apache.accumulo.core.Constants.NO_AUTHS);
        root.permissions.add(SystemPermission.SYSTEM);
        users.put(root.name, root);
        createTable("root", Constants.METADATA_TABLE_NAME, true, TimeType.LOGICAL);
    }

    public org.apache.hadoop.fs.FileSystem getFileSystem() {
        return fs;
    }

    void setProperty(java.lang.String key, java.lang.String value) {
        systemProperties.put(key, value);
    }

    java.lang.String removeProperty(java.lang.String key) {
        return systemProperties.remove(key);
    }

    void createTable(java.lang.String user, java.lang.String table) {
        createTable(user, table, true, TimeType.MILLIS);
    }

    public void addMutation(java.lang.String table, org.apache.accumulo.core.data.Mutation m) {
        org.apache.accumulo.core.client.mock.MockTable t = tables.get(table);
        t.addMutation(m);
    }

    public org.apache.accumulo.core.client.BatchScanner createBatchScanner(java.lang.String tableName, org.apache.accumulo.core.security.Authorizations authorizations) {
        return new org.apache.accumulo.core.client.mock.MockBatchScanner(tables.get(tableName), authorizations);
    }

    public void createTable(java.lang.String username, java.lang.String tableName, boolean useVersions, org.apache.accumulo.core.client.admin.TimeType timeType) {
        org.apache.accumulo.core.client.mock.MockTable t = new org.apache.accumulo.core.client.mock.MockTable(useVersions, timeType, java.lang.Integer.toString(tableIdCounter.incrementAndGet()));
        t.userPermissions.put(username, java.util.EnumSet.allOf(org.apache.accumulo.core.security.TablePermission.class));
        tables.put(tableName, t);
    }

    public void addSplits(java.lang.String tableName, java.util.SortedSet<org.apache.hadoop.io.Text> partitionKeys) {
        tables.get(tableName).addSplits(partitionKeys);
    }

    public java.util.Collection<org.apache.hadoop.io.Text> getSplits(java.lang.String tableName) {
        return tables.get(tableName).getSplits();
    }

    public void merge(java.lang.String tableName, org.apache.hadoop.io.Text start, org.apache.hadoop.io.Text end) {
        tables.get(tableName).merge(start, end);
    }
}
