public class DefaultScheduledPollConsumerScheduler extends org.apache.camel.support.ServiceSupport implements org.apache.camel.spi.ScheduledPollConsumerScheduler {
    private static final org.slf4j.Logger LOG = org.slf4j.LoggerFactory.getLogger(org.apache.camel.impl.DefaultScheduledPollConsumerScheduler.class);

    private org.apache.camel.CamelContext camelContext;

    private org.apache.camel.Consumer consumer;

    private java.util.concurrent.ScheduledExecutorService scheduledExecutorService;

    private boolean shutdownExecutor;

    private volatile java.util.concurrent.ScheduledFuture<?> future;

    private java.lang.Runnable task;

    private long initialDelay = 1000;

    private long delay = 500;

    private java.util.concurrent.TimeUnit timeUnit = java.util.concurrent.TimeUnit.MILLISECONDS;

    private boolean useFixedDelay = true;

    public org.apache.camel.CamelContext getCamelContext() {
        return camelContext;
    }

    public void setCamelContext(org.apache.camel.CamelContext camelContext) {
        this.camelContext = camelContext;
    }

    public long getInitialDelay() {
        return initialDelay;
    }

    public void setInitialDelay(long initialDelay) {
        this.initialDelay = initialDelay;
    }

    public long getDelay() {
        return delay;
    }

    public void setDelay(long delay) {
        this.delay = delay;
    }

    public java.util.concurrent.TimeUnit getTimeUnit() {
        return timeUnit;
    }

    public void setTimeUnit(java.util.concurrent.TimeUnit timeUnit) {
        this.timeUnit = timeUnit;
    }

    public boolean isUseFixedDelay() {
        return useFixedDelay;
    }

    public void setUseFixedDelay(boolean useFixedDelay) {
        this.useFixedDelay = useFixedDelay;
    }

    public java.util.concurrent.ScheduledExecutorService getScheduledExecutorService() {
        return scheduledExecutorService;
    }

    public void setScheduledExecutorService(java.util.concurrent.ScheduledExecutorService scheduledExecutorService) {
        this.scheduledExecutorService = scheduledExecutorService;
    }

    @java.lang.Override
    public void onInit(org.apache.camel.Consumer consumer) {
        this.consumer = consumer;
    }

    @java.lang.Override
    public void scheduleTask(java.lang.Runnable task) {
        this.task = task;
    }

    @java.lang.Override
    public void unscheduleTask() {
        if (future != null) {
            future.cancel(false);
        }
    }

    @java.lang.Override
    public void startScheduler() {
        if (future == null) {
            if (isUseFixedDelay()) {
                if (org.apache.camel.impl.DefaultScheduledPollConsumerScheduler.LOG.isDebugEnabled()) {
                    org.apache.camel.impl.DefaultScheduledPollConsumerScheduler.LOG.debug("Scheduling poll (fixed delay) with initialDelay: {}, delay: {} ({}) for: {}", new java.lang.Object[]{ getInitialDelay(), getDelay(), getTimeUnit().name().toLowerCase(java.util.Locale.ENGLISH), consumer.getEndpoint() });
                }
                future = scheduledExecutorService.scheduleWithFixedDelay(task, getInitialDelay(), getDelay(), getTimeUnit());
            } else {
                if (org.apache.camel.impl.DefaultScheduledPollConsumerScheduler.LOG.isDebugEnabled()) {
                    org.apache.camel.impl.DefaultScheduledPollConsumerScheduler.LOG.debug("Scheduling poll (fixed rate) with initialDelay: {}, delay: {} ({}) for: {}", new java.lang.Object[]{ getInitialDelay(), getDelay(), getTimeUnit().name().toLowerCase(java.util.Locale.ENGLISH), consumer.getEndpoint() });
                }
                future = scheduledExecutorService.scheduleAtFixedRate(task, getInitialDelay(), getDelay(), getTimeUnit());
            }
        }
    }

    @java.lang.Override
    public boolean isSchedulerStarted() {
        return future != null;
    }

    @java.lang.Override
    protected void doStart() throws java.lang.Exception {
        org.apache.camel.util.ObjectHelper.notNull(consumer, "Consumer", this);
        org.apache.camel.util.ObjectHelper.notNull(camelContext, "CamelContext", this);
        org.apache.camel.util.ObjectHelper.notNull(task, "Task", this);
        if (scheduledExecutorService == null) {
            this.scheduledExecutorService = getCamelContext().getExecutorServiceManager().newSingleThreadScheduledExecutor(consumer, consumer.getEndpoint().getEndpointUri());
            this.shutdownExecutor = true;
        }
    }

    @java.lang.Override
    protected void doStop() throws java.lang.Exception {
        if (future != null) {
            org.apache.camel.impl.DefaultScheduledPollConsumerScheduler.LOG.debug("This consumer is stopping, so cancelling scheduled task: " + future);
            future.cancel(false);
            future = null;
        }
    }

    @java.lang.Override
    protected void doShutdown() throws java.lang.Exception {
        if (shutdownExecutor && (scheduledExecutorService != null)) {
            getCamelContext().getExecutorServiceManager().shutdownNow(scheduledExecutorService);
            scheduledExecutorService = null;
            future = null;
        }
    }
}

