    private void copyNamespaces(org.apache.jackrabbit.oak.spi.state.NodeBuilder root, java.util.Map<java.lang.String, java.lang.String> uriToPrefix, java.util.Map<java.lang.Integer, java.lang.String> idxToPrefix) throws javax.jcr.RepositoryException {
        org.apache.jackrabbit.oak.spi.state.NodeBuilder system = root.child(org.apache.jackrabbit.JcrConstants.JCR_SYSTEM);
        org.apache.jackrabbit.oak.spi.state.NodeBuilder namespaces = system.child(NamespaceConstants.REP_NAMESPACES);
        java.util.Properties registry = loadProperties("/namespaces/ns_reg.properties");
        java.util.Properties indexes = loadProperties("/namespaces/ns_idx.properties");
        for (java.lang.String prefixHint : registry.stringPropertyNames()) {
            java.lang.String prefix;
            java.lang.String uri = registry.getProperty(prefixHint);
            if (".empty.key".equals(prefixHint)) {
                prefix = "";
            } else {
                prefix = addCustomMapping(namespaces, uri, prefixHint);
            }
            java.lang.String index = null;
            if (uri.isEmpty()) {
                index = indexes.getProperty(".empty.key");
            }
            if (index == null) {
                index = indexes.getProperty(uri);
            }
            java.lang.Integer idx;
            if (index != null) {
                idx = java.lang.Integer.decode(index);
            } else {
                int i = 0;
                do {
                    idx = (uri.hashCode() + (i++)) & 0xffffff;
                } while (idxToPrefix.containsKey(idx) );
            }
            com.google.common.base.Preconditions.checkState(uriToPrefix.put(uri, prefix) == null);
            com.google.common.base.Preconditions.checkState(idxToPrefix.put(idx, prefix) == null);
        }
        org.apache.jackrabbit.oak.plugins.name.Namespaces.buildIndexNode(namespaces);
    }

    private java.util.Properties loadProperties(java.lang.String path) throws javax.jcr.RepositoryException {
