        } catch (org.apache.jackrabbit.core.fs.FileSystemException e) {
            throw new javax.jcr.RepositoryException(e);
        } catch (java.io.IOException e) {
            throw new javax.jcr.RepositoryException(e);
        }
        return properties;
    }

