        java.lang.String sep = "";
        for (org.apache.jackrabbit.spi.Path.Element element : path.getElements()) {
            if (element.denotesRoot()) {
                oakPath.append('/');
                continue;
            } else if (element.denotesName()) {
                oakPath.append(sep).append(nameMapper.getOakName(element.getString()));
            } else if (element.denotesCurrent()) {
                oakPath.append(sep).append('.');
            } else if (element.denotesParent()) {
                oakPath.append(sep).append("..");
            } else {
                throw new javax.jcr.UnsupportedRepositoryOperationException("Cannot copy default value " + path);
            }
            sep = "/";
        }
        return oakPath.toString();
    }

