        if (!types.hasChildNode(oakName)) {
            org.apache.jackrabbit.spi.QNodeTypeDefinition def = sourceRegistry.getNodeTypeDef(name);
            org.apache.jackrabbit.oak.spi.state.NodeBuilder type = types.child(oakName);
            copyNodeType(def, type, prefixToUri);
        }
    }
}

private void copyNodeType(org.apache.jackrabbit.spi.QNodeTypeDefinition def, org.apache.jackrabbit.oak.spi.state.NodeBuilder builder, java.util.Map<java.lang.String, java.lang.String> prefixToUri) throws javax.jcr.RepositoryException {
    builder.setProperty(org.apache.jackrabbit.JcrConstants.JCR_PRIMARYTYPE, org.apache.jackrabbit.JcrConstants.NT_NODETYPE, org.apache.jackrabbit.oak.upgrade.NAME);
    builder.setProperty(org.apache.jackrabbit.JcrConstants.JCR_NODETYPENAME, getOakName(def.getName()), org.apache.jackrabbit.oak.upgrade.NAME);
    org.apache.jackrabbit.spi.Name[] supertypes = def.getSupertypes();
    if ((supertypes != null) && (supertypes.length > 0)) {
        java.util.List<java.lang.String> names = com.google.common.collect.Lists.newArrayListWithCapacity(supertypes.length);
        for (org.apache.jackrabbit.spi.Name supertype : supertypes) {
            names.add(getOakName(supertype));
        }
        builder.setProperty(org.apache.jackrabbit.JcrConstants.JCR_SUPERTYPES, names, org.apache.jackrabbit.oak.upgrade.NAMES);
    }
    builder.setProperty(org.apache.jackrabbit.oak.upgrade.JCR_IS_ABSTRACT, def.isAbstract());
    builder.setProperty(org.apache.jackrabbit.oak.upgrade.JCR_IS_QUERYABLE, def.isQueryable());
    builder.setProperty(org.apache.jackrabbit.JcrConstants.JCR_ISMIXIN, def.isMixin());
    builder.setProperty(org.apache.jackrabbit.JcrConstants.JCR_HASORDERABLECHILDNODES, def.hasOrderableChildNodes());
    org.apache.jackrabbit.spi.Name primary = def.getPrimaryItemName();
    if (primary != null) {
        builder.setProperty(org.apache.jackrabbit.JcrConstants.JCR_PRIMARYITEMNAME, getOakName(primary), org.apache.jackrabbit.oak.upgrade.NAME);
    }
    org.apache.jackrabbit.spi.QPropertyDefinition[] properties = def.getPropertyDefs();
    for (int i = 0; i < properties.length; i++) {
        java.lang.String name = ((org.apache.jackrabbit.JcrConstants.JCR_PROPERTYDEFINITION + '[') + (i + 1)) + ']';
        copyPropertyDefinition(properties[i], builder.child(name), prefixToUri);
    }
    org.apache.jackrabbit.spi.QNodeDefinition[] childNodes = def.getChildNodeDefs();
    for (int i = 0; i < childNodes.length; i++) {
        java.lang.String name = ((org.apache.jackrabbit.JcrConstants.JCR_CHILDNODEDEFINITION + '[') + (i + 1)) + ']';
        copyChildNodeDefinition(childNodes[i], builder.child(name));
    }
}

private void copyPropertyDefinition(org.apache.jackrabbit.spi.QPropertyDefinition def, org.apache.jackrabbit.oak.spi.state.NodeBuilder builder, java.util.Map<java.lang.String, java.lang.String> prefixToUri) throws javax.jcr.RepositoryException {
    builder.setProperty(org.apache.jackrabbit.JcrConstants.JCR_PRIMARYTYPE, org.apache.jackrabbit.JcrConstants.NT_PROPERTYDEFINITION, org.apache.jackrabbit.oak.upgrade.NAME);
    copyItemDefinition(def, builder);
    builder.setProperty(org.apache.jackrabbit.JcrConstants.JCR_REQUIREDTYPE, org.apache.jackrabbit.oak.api.Type.fromTag(def.getRequiredType(), false).toString());
    org.apache.jackrabbit.spi.QValueConstraint[] constraints = def.getValueConstraints();
    if ((constraints != null) && (constraints.length > 0)) {
        java.util.List<java.lang.String> strings = com.google.common.collect.Lists.newArrayListWithCapacity(constraints.length);
        for (org.apache.jackrabbit.spi.QValueConstraint constraint : constraints) {
            strings.add(constraint.getString());
        }
        builder.setProperty(org.apache.jackrabbit.JcrConstants.JCR_VALUECONSTRAINTS, strings, org.apache.jackrabbit.oak.upgrade.STRINGS);
    }
    org.apache.jackrabbit.spi.QValue[] qValues = def.getDefaultValues();
    if (qValues != null) {
        org.apache.jackrabbit.oak.upgrade.RepositoryUpgrade.copyDefaultValues(qValues, builder, new org.apache.jackrabbit.oak.namepath.GlobalNameMapper(prefixToUri));
    }
    builder.setProperty(org.apache.jackrabbit.JcrConstants.JCR_MULTIPLE, def.isMultiple());
    java.util.List<java.lang.String> operators = java.util.Arrays.asList(def.getAvailableQueryOperators());
    builder.setProperty(org.apache.jackrabbit.oak.upgrade.JCR_AVAILABLE_QUERY_OPERATORS, operators, org.apache.jackrabbit.oak.upgrade.NAMES);
    builder.setProperty(org.apache.jackrabbit.oak.upgrade.JCR_IS_FULLTEXT_SEARCHABLE, def.isFullTextSearchable());
    builder.setProperty(org.apache.jackrabbit.oak.upgrade.JCR_IS_QUERY_ORDERABLE, def.isQueryOrderable());
}
